import React, { useState, useEffect } from 'react';
import { format, startOfWeek, addDays, isSameDay, isToday } from 'date-fns';
import { de } from 'date-fns/locale';
import { ChevronLeft, ChevronRight, Plus, User, Home, Plane, Heart, AlertTriangle } from 'lucide-react';
import { Employee, AttendanceEntry, AttendanceStatus } from '../types';
import { storage } from '../utils/storage';

interface AttendanceCalendarProps {
  selectedDate: Date;
  onDateSelect: (date: Date) => void;
}

const AttendanceCalendar: React.FC<AttendanceCalendarProps> = ({ selectedDate, onDateSelect }) => {
  const [employees, setEmployees] = useState<Employee[]>([]);
  const [attendance, setAttendance] = useState<AttendanceEntry[]>([]);
  const [currentWeek, setCurrentWeek] = useState<Date[]>([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<AttendanceStatus>('present');

  useEffect(() => {
    loadData();
    generateWeekDays();
  }, []);

  useEffect(() => {
    generateWeekDays();
  }, [selectedDate]);

  const loadData = () => {
    setEmployees(storage.getEmployees());
    setAttendance(storage.getAttendance());
  };

  const generateWeekDays = () => {
    const weekStart = startOfWeek(selectedDate, { weekStartsOn: 1 });
    const days = [];
    for (let i = 0; i < 7; i++) {
      days.push(addDays(weekStart, i));
    }
    setCurrentWeek(days);
  };

  const getStatusIcon = (status: AttendanceStatus) => {
    switch (status) {
      case 'present':
        return <User className="w-4 h-4 text-success-600" />;
      case 'remote':
        return <Home className="w-4 h-4 text-primary-600" />;
      case 'vacation':
        return <Plane className="w-4 h-4 text-warning-600" />;
      case 'sick':
        return <Heart className="w-4 h-4 text-red-600" />;
      case 'absent':
        return <AlertTriangle className="w-4 h-4 text-gray-500" />;
      default:
        return null;
    }
  };

  const getStatusColor = (status: AttendanceStatus) => {
    switch (status) {
      case 'present':
        return 'bg-success-100 text-success-800 border-success-200';
      case 'remote':
        return 'bg-primary-100 text-primary-800 border-primary-200';
      case 'vacation':
        return 'bg-warning-100 text-warning-800 border-warning-200';
      case 'sick':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'absent':
        return 'bg-gray-100 text-gray-800 border-gray-200';
      default:
        return 'bg-gray-50 text-gray-500 border-gray-200';
    }
  };

  const getStatusText = (status: AttendanceStatus) => {
    switch (status) {
      case 'present':
        return 'Anwesend';
      case 'remote':
        return 'Remote';
      case 'vacation':
        return 'Urlaub';
      case 'sick':
        return 'Krank';
      case 'absent':
        return 'Abwesend';
      default:
        return 'Nicht eingetragen';
    }
  };

  const getAttendanceForEmployeeAndDate = (employeeId: string, date: Date) => {
    const dateStr = format(date, 'yyyy-MM-dd');
    return attendance.find(entry => 
      entry.employeeId === employeeId && entry.date === dateStr
    );
  };

  const handleAddAttendance = () => {
    if (!selectedEmployee) return;

    const newEntry: AttendanceEntry = {
      id: Date.now().toString(),
      employeeId: selectedEmployee,
      date: format(selectedDate, 'yyyy-MM-dd'),
      status: selectedStatus,
    };

    storage.addAttendanceEntry(newEntry);
    setAttendance(storage.getAttendance());
    setShowAddModal(false);
    setSelectedEmployee('');
  };

  const handlePreviousWeek = () => {
    const newDate = addDays(selectedDate, -7);
    onDateSelect(newDate);
  };

  const handleNextWeek = () => {
    const newDate = addDays(selectedDate, 7);
    onDateSelect(newDate);
  };

  return (
    <div className="space-y-6">
      {/* Kalender-Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <button
            onClick={handlePreviousWeek}
            className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <ChevronLeft className="w-5 h-5" />
          </button>
          
          <h2 className="text-xl font-semibold text-gray-900">
            {currentWeek.length > 0 ? (
              `${format(currentWeek[0], 'dd.MM.yyyy', { locale: de })} - ${format(currentWeek[6], 'dd.MM.yyyy', { locale: de })}`
            ) : (
              'Lade Kalender...'
            )}
          </h2>
          
          <button
            onClick={handleNextWeek}
            className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <ChevronRight className="w-5 h-5" />
          </button>
        </div>

        <button
          onClick={() => setShowAddModal(true)}
          className="btn-primary flex items-center"
        >
          <Plus className="w-4 h-4 mr-2" />
          Eintrag hinzuf체gen
        </button>
      </div>

      {/* Kalender-Tabelle */}
      <div className="card overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-4 py-3 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                  Mitarbeiter
                </th>
                {currentWeek.map((date) => (
                  <th key={date.toISOString()} className="px-2 py-3 text-center text-sm font-medium text-gray-500 uppercase tracking-wider">
                    <div className="flex flex-col items-center">
                      <span className="text-xs text-gray-400">
                        {format(date, 'EEE', { locale: de })}
                      </span>
                      <span className={`text-sm font-medium ${
                        isToday(date) ? 'text-primary-600 bg-primary-100 rounded-full w-8 h-8 flex items-center justify-center' : ''
                      }`}>
                        {format(date, 'dd')}
                      </span>
                    </div>
                  </th>
                ))}
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {employees.map((employee) => (
                <tr key={employee.id} className="hover:bg-gray-50">
                  <td className="px-4 py-3 whitespace-nowrap">
                    <div className="flex items-center">
                      <div className="flex-shrink-0 h-8 w-8">
                        <div className="h-8 w-8 rounded-full bg-primary-100 flex items-center justify-center">
                          <span className="text-sm font-medium text-primary-700">
                            {employee.name.split(' ').map(n => n[0]).join('')}
                          </span>
                        </div>
                      </div>
                      <div className="ml-3">
                        <div className="text-sm font-medium text-gray-900">
                          {employee.name}
                        </div>
                        <div className="text-sm text-gray-500">
                          {employee.department}
                        </div>
                      </div>
                    </div>
                  </td>
                  {currentWeek.map((date) => {
                    const entry = getAttendanceForEmployeeAndDate(employee.id, date);
                    return (
                      <td key={date.toISOString()} className="px-2 py-3 text-center">
                        {entry ? (
                          <div className="flex items-center justify-center">
                            <div className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium border ${getStatusColor(entry.status)}`}>
                              {getStatusIcon(entry.status)}
                              <span className="ml-1">{getStatusText(entry.status)}</span>
                            </div>
                          </div>
                        ) : (
                          <div className="text-gray-400 text-xs">
                            -
                          </div>
                        )}
                      </td>
                    );
                  })}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal f체r neue Eintr채ge */}
      {showAddModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">
                Anwesenheit eintragen
              </h3>
              
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Mitarbeiter
                  </label>
                  <select
                    value={selectedEmployee}
                    onChange={(e) => setSelectedEmployee(e.target.value)}
                    className="input-field"
                  >
                    <option value="">Mitarbeiter ausw채hlen</option>
                    {employees.map((employee) => (
                      <option key={employee.id} value={employee.id}>
                        {employee.name} ({employee.department})
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Status
                  </label>
                  <select
                    value={selectedStatus}
                    onChange={(e) => setSelectedStatus(e.target.value as AttendanceStatus)}
                    className="input-field"
                  >
                    <option value="present">Anwesend</option>
                    <option value="remote">Remote</option>
                    <option value="vacation">Urlaub</option>
                    <option value="sick">Krank</option>
                    <option value="absent">Abwesend</option>
                  </select>
                </div>

                <div className="flex justify-end space-x-3 pt-4">
                  <button
                    onClick={() => setShowAddModal(false)}
                    className="btn-secondary"
                  >
                    Abbrechen
                  </button>
                  <button
                    onClick={handleAddAttendance}
                    disabled={!selectedEmployee}
                    className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Speichern
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AttendanceCalendar;
